import React, { useState } from 'react';

const Item = (props) => {
  const [status, setStatus] = useState(false);

  return (
    <div style={{
      display: 'flex',
      justifyContent:'space-between',
      alignItems: 'center'
    }}>
      <li
        key={props.key}
        id={props.id}
        onClick={() => setStatus(!status)}
        style={{textDecorationLine: status ? 'line-through' : 'none'}}
      >
        {props.value}
      </li>
      <button style={{
        width: '30px',
        height: '30px'
      }} onClick={deleteItem}>x</button>
    </div>

  );
};

export default Item;
