import React, { useState } from "react";
import Item from './Item';

function App() {
  const [input, setInput] = useState("");
  const [list, setList] = useState([]);
  let addedItem;

  const deleteItem = (id) => {
    setList(() => {
      list.filter(item => item.id != id);
    })
  };

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={(event) => setInput(event.target.value)} type="text" />
        <button
          onClick={() => {
            addedItem = input
            setList([...list, addedItem])
          }}
        >
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          {list.map((item, index) => <Item key={index + 1} id={index + 1} value={item} function={() => deleteItem(index)}/>)}
        </ul>
      </div>
    </div>
  );
}

export default App;
