import React, { useState } from 'react';

const Form = (props) => {
    const [input, setInput] = useState("");

    return (
        <div className="form">
            <input value={input} onChange={(event) => setInput(event.target.value)} type="text" />
            <button onClick={() => {
                props.buttonOnClick(props.inputValue);
                setInput("");
            }}>
                <span>Add</span>
            </button>
      </div>
    );
};

export default Form;
