import React, { useState } from "react";

function App() {
  const [input, setInput] = useState("");

  let list = [];
  let addedItem;

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={ (event) => {
          setInput(event.target.value)
          console.log(input)
          } } type="text" />
        <button onClick={ () => {
          addedItem = input
          list.push(addedItem)
          console.log(addedItem)
          } }>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          <li>A Item</li>
        </ul>
      </div>
    </div>
  );
}

export default App;
