import React, { useState } from "react";

function App() {
  const [input, setInput] = useState("");
  const [list, setList] = useState([]);

  let addedItem;

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={(event) => setInput(event.target.value)} type="text" />
        <button
          onClick={() => {
            addedItem = input
            setList([...list, addedItem])
          }}
        >
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          {list.map((index, item) => <li key={index + 1}>{item}</li>)}
        </ul>
      </div>
    </div>
  );
}

export default App;
