const Form = () => {
    const [input, setInput] = useState("");
    let list = [];
    let addedItem;

    return (
      <div className="form">
        <input onChange={ (event) => {
          setInput(event.target.value)
          console.log(input)
          } } type="text" />
        <button onClick={ () => {
          addedItem = input
          list.push(addedItem)
          } }>
          <span>Add</span>
        </button>
      </div>
    );
};

export default Form;
